mapper.update:
update products
jibun=#{product.jibun}, house_type=#{product.houseType}, trade_type=#{product.tradeType}, name=#{product.name}, build_year=#{product.buildYear}, exclu_use_ar=#{product.excluUseAr}, apt_dong=#{product.aptDong}, floor=#{product.floor}, land_ar=#{product.landAr}, total_floor_ar=#{product.totalFloorAr}, plottage_ar=#{product.plottageAr}, deal_amount=#{product.dealAmount}, deposit=#{product.deposit}, monthly_rent=#{product.monthlyRent}, sgg_cd=#{product.sggCd}, umd_nm=#{product.umdNm}, agent_id=#{product.agentId},
where product_id = #{id};
