mapper.update:
UPDATE reservations
SET
	agent_payment_key=#{agentPaymentKey},
	status=#{status}
WHERE reservation_id=#{reservationId}

UPDATE reservations
user_confirmed = #{status}, agent_confirmed = #{status},
WHERE reservation_id = #{reservationId}

UPDATE reservations
SET status=#{status}
WHERE reservation_id=#{reservationId}

UPDATE reservations
user_confirmed = 'N', agent_confirmed = 'N', status=#{status}, reported_at=#{reportedAt},
WHERE reservation_id=#{reservationId}
