mapper.select:
SELECT u.user_id, u.email, u.name, u.phone, u.birth, u.role, a.ceo_name, a.realtor_agency, a.business_number
FROM users u LEFT JOIN agents a ON u.user_id = a.agent_id
WHERE u.user_id = #{userId}

mapper.update:
UPDATE users
name=#{name}, phone=#{phone}, birth=#{birth},
WHERE user_id = #{userId}

UPDATE agents
ceo_name=#{ceoName}, realtor_agency=#{realtorAgency}, business_number=#{businessNumber},
WHERE agent_id = #{agentId}

UPDATE users
SET password=#{encodedPassword}
WHERE user_id = #{userId}
